import React from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import { useState } from "react";
import { Redirect } from "react-router-dom/cjs/react-router-dom.min";

export const Contact = () => {
    const [statue, setStatue] = useState('');
    const [objet, setObjet] = useState('');
    const [msg, setMsg] = useState('');

    const Handleclick = () => {
        if (objet && msg) {
            setStatue('send');
        }
    }

    if (statue === 'send') {
        return (
            <p>Message send !</p>
        );
    }
    return (
        <div className="contact">
            <form onSubmit={Handleclick}>
                <p></p>
                <label htmlFor="email">Objet</label><p></p>
                <input value={objet} onChange={(e) => setObjet(e.target.value)} type="text" placeholder="Objet" /><p></p>
                <label htmlFor="text">Message</label><p></p>
                <input value={msg} onChange={(e) => setMsg(e.target.value)}type="text" placeholder="Message"/><p></p>
                <button type="submit">Envoyer</button>
            </form>
        </div>
    );
}
