import React from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import { Redirect } from 'react-router-dom';
import { useState } from "react";

export const Register = () => {
    const [statue, Setstatue] = useState('False');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [nom, setNom] = useState('');
    const [prenom, setPrenom] = useState('');

    const Handleclick = () => {
        if (email && password && nom && prenom) {
            Setstatue('True');
        } else {
            Setstatue('False')
        }
    }

    if (statue === 'True') {
        return <Redirect to="/"/>;
    }
    if (statue === 'True') {
    } else {
        return (
            <div className="register">
                <form onSubmit={Handleclick}>
                    <p></p>
                    <label htmlFor="email">Email</label><p></p>
                    <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="Email" /><p></p>
                    <label htmlFor="Password">Password</label><p></p>
                    <input value={password} onChange={(e) => setPassword(e.target.value)}type="password" placeholder="**********"/><p></p>
                    <label htmlFor="Nom">Nom</label><p></p>
                    <input value={nom} onChange={(e) => setNom(e.target.value)}type="text" placeholder="Nom"/><p></p>
                    <label htmlFor="prenom">Prénom</label><p></p>
                    <input value={prenom} onChange={(e) => setPrenom(e.target.value)}type="text" placeholder="Prénom"/><p></p>
                    <button type="submit">Register</button>
                    <Link to="/login">
                        <button>Already an account ?</button>
                    </Link>
                </form>
            </div>
        );
    }
}
